"use strict";$("form").on("submit",function(e){e.preventDefault();var t=$(".username").val(),e=$(".password").val();if(!t||!e)return alert("请完整填写表单");$.ajax({url:"http://localhost:8888/users/login",method:"POST",data:{username:t,password:e},success:function(e){0===e.code?$(".error").css("display","block"):(window.localStorage.setItem("id",e.user.id),window.localStorage.setItem("token",e.token),alert("注册成功, 点击确定跳转到登录页"),window.location.href="./index.html")}})});